import { Box } from "@mui/material";
import React, { HTMLAttributes, useEffect, useState } from "react";

const RandomQuotes: React.FC<Props> = ({ click }) => {

  const [quote, setQuote] = useState<string>("Random");
  const[random, setRandom] = useState<boolean>(false);


  const quotes = [
    "The only limit to our realization of tomorrow is our doubts of today.",
    "The future belongs to those who believe in the beauty of their dreams.",
    "In the end, we will remember not the words of our enemies, but the silence of our friends.",
  ];
  console.log("rerender outside useeffect");

  useEffect(() => {
    console.log("rerender inside useeffect");
    let index = Math.floor(Math.random() * 3);
    setQuote(quotes[index]);
  }, []);

  return (
    <>
      <Box sx={{ padding: "1rem", backgroundColor: "red" }} onClick={() => setRandom((prev) => !prev)}>
        {quote + "  " + click+" " + random}
      </Box>
    </>
  );
};
interface Props extends HTMLAttributes<any> {
  click: number;
}
export default RandomQuotes;
